<?php  $con = require 'database.php';  $id = $name = $surname =$curs = $image='';  if ($_SERVER['REQUEST_METHOD']=='GET'){      $id = $_GET['id'];      $sql = "select *from talaba where id = $id";      $result = $con->query($sql);      $a = [];      if ($row = $result->fetch_assoc()){          $a =$row;      }  }  $con->close();?><!DOCTYPE html><html><head>    <meta charset="utf-8">    <meta name="viewport" content="width=device-width, initial-scale=1">    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@4.6.1/dist/css/bootstrap.min.css">    <title></title></head><body><div class="container">    <form action="update.php" method="POST" enctype="multipart/form-data">        <input type="hidden" name="id" value="<?php echo  $a['id'] ?>">        <div class="form-group">            <label>name</label>            <input type="text" class="form-control" name="name" value="<?php echo  $a['name'] ?>">        </div>        <div class="form-group">            <label>surname</label>            <input type="text" class="form-control" name="surname" value="<?php echo  $a['surname'] ?>">        </div>        <div class="form-group">            <label>curs</label>            <input type="text" class="form-control" name="curs" value="<?php echo  $a['curs'] ?>">        </div>        <div class="form-group">            <label>image</label>            <input type="file" class="form-control" name="image">        </div>        <input type="submit" value="update student" class="btn btn-primary">    </form></div></body></html>