<?php$id = $name = $surname = $curs ='';$con = require  'database.php';if ($_SERVER['REQUEST_METHOD']=='POST'){    $id = $_POST['id'];    $name = $_POST['name'];    $surname = $_POST['surname'];    $curs = $_POST['curs'];    $sql = "update talaba set name = '$name', surname = '$surname',curs ='$curs' where id = $id";    $result = $con->query($sql);    if ($result){        header('location:index.php');    }}$con->close();?>